public class PrintArray        {          public void print()          {            int[][] matrix = {{1,1}, {2,4}, {3,9}};            for(int i=0; i<matrix.length; i++)            {              for(int j=0; j<matrix[i].length; j++)              {                System.out.print(matrix[i][j] + ", ");           }              System.out.println(" ");            } }          public static void main(String[] args)          {            PrintArray pa = new PrintArray();            pa.print();          } }